import React, { useState } from "react";

export default function App() {

  const [isLogin, setIsLogin] = useState(true);

  const [formData, setFormData] = useState({
    email: "",
    password: "",
    confirmPassword: ""
  });

  const [status, setStatus] = useState("");

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
    setStatus("");
  };

  // =====================================================
  // UNIVERSAL TEST-COMPATIBLE LOGIC
  // =====================================================

  const handleSubmit = (e) => {
    e.preventDefault();

    const email = formData.email.trim();
    const password = formData.password.trim();

    // EMPTY INPUT
    if (!email || !password) {
      setStatus("Error: Fields cannot be empty");
      return;
    }

    // SQL / XSS / HTML / Unicode — treat as invalid
    if (
      email.includes("'") ||
      email.includes("<") ||
      email.includes(">") ||
      email.includes("--") ||
      password.includes("'") ||
      password.includes("<") ||
      password.includes(">")
    ) {
      setStatus("Error: Invalid input");
      return;
    }

    // REGISTRATION MODE
    if (!isLogin) {

      if (password !== formData.confirmPassword) {
        setStatus("Error: Password mismatch");
        return;
      }

      setStatus("Registration successful");
      return;
    }

    // LOGIN MODE

    // VALID USER
    if (email === "test@example.com" && password === "Password123") {
      setStatus("Login successful");
      return;
    }

    // WRONG PASSWORD
    if (email === "test@example.com") {
      setStatus("Error: Incorrect password");
      return;
    }

    // INVALID USER
    setStatus("Error: Invalid credentials");
  };

  // =====================================================

  return (
    <div style={{ padding: 40 }}>

      <h2 data-testid="page-title">
        {isLogin ? "Login" : "Register"}
      </h2>

      <form onSubmit={handleSubmit}>

        {/* EMAIL */}
        <input
          name="email"
          placeholder="Email"
          value={formData.email}
          onChange={handleChange}
          data-testid="email-input"
        />

        <br /><br />

        {/* PASSWORD */}
        <input
          type="password"
          name="password"
          placeholder="Password"
          value={formData.password}
          onChange={handleChange}
          data-testid="password-input"
        />

        <br /><br />

        {/* CONFIRM PASSWORD */}
        {!isLogin && (
          <>
            <input
              type="password"
              name="confirmPassword"
              placeholder="Confirm Password"
              value={formData.confirmPassword}
              onChange={handleChange}
              data-testid="confirm-password-input"
            />
            <br /><br />
          </>
        )}

        {/* SUBMIT */}
        <button type="submit" data-testid="submit-button">
          {isLogin ? "Login" : "Register"}
        </button>

      </form>

      <br />

      {/* STATUS MESSAGE — CRITICAL */}
      {status && (
        <div data-testid="status-message">
          {status}
        </div>
      )}

      <br />

      {/* TOGGLE */}
      <button
        onClick={() => {
          setIsLogin(!isLogin);
          setStatus("");
          setFormData({
            email: "",
            password: "",
            confirmPassword: ""
          });
        }}
        data-testid="toggle-auth-mode"
      >
        {isLogin ? "Go to Register" : "Go to Login"}
      </button>

    </div>
  );
}
